signal = 4
message = 14


def getPosition(size, signal):
    count = 0
    for i in range(size, len(signal), 1):
        examine = list(set(signal[count:i]))
        if (len(examine) == size):
            return i

        count += 1

    return 0


result = getPosition(message, '')

print(result)


def test():
    assert getPosition(message, 'mjqjpqmgbljsphdztnvjfqwrcgsmlb') == 19


def test1():
    assert getPosition(message, 'bvwbjplbgvbhsrlpgdmjqwftvncz') == 23


