size = 14


def solve(signal):
    count = 0
    for i in range(size, len(signal), 1):
        examine = list(set(signal[count:i]))
        if (len(examine) == size):
            return i

        count += 1

    return 0


result = solve('')

print(result)


def test():
    assert solve('mjqjpqmgbljsphdztnvjfqwrcgsmlb') == 19


def test1():
    assert solve('bvwbjplbgvbhsrlpgdmjqwftvncz') == 23


